import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent {

}
<body>
    <div class="row">
    <div class="container">
        <img src="../../assets/bat.jpg" alt="Responsive image">
    </div>
    <div class="card shadow">
        <img class="card-img" src="../../assets/dhoni.jpg" alt="Bologna">
        <div class="card-img-overlay text-white d-flex flex-column justify-content-center">
         <div class="card-header bg-info text-white"><h4>ALMONDS</h4></div>
         <p class="card-text"><b>Almonds are rich in valuable nutrients for your body, like magnesium, vitamin E, and dietary fiber. A single serving of almonds makes for a nutritious and filling snack. Almonds have calcium and phosphorus, which improve bone health and can protect you from fractures.</b></p>
        </div>
     </div>
    </div>

</body>
