import { ViewChild } from '@angular/core';
import { ElementRef } from '@angular/core';
import { Component } from '@angular/core';

@Component({
  selector: 'app-kabaddi',
  templateUrl: './kabaddi.component.html',
  styleUrls: ['./kabaddi.component.css']
})
export class KabaddiComponent {
  date: any;
  now: any;
  targetDate: any = new Date(2023, 5, 11);
  targetTime: any = this.targetDate.getTime();
  difference: number;
  months: Array<string> = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December',
  ];
  currentTime: any = `${
    this.months[this.targetDate.getMonth()]
  } ${this.targetDate.getDate()}, ${this.targetDate.getFullYear()}`;

  @ViewChild('days', { static: true }) days: ElementRef;
  @ViewChild('hours', { static: true }) hours: ElementRef;
  @ViewChild('minutes', { static: true }) minutes: ElementRef;
  @ViewChild('seconds', { static: true }) seconds: ElementRef;

  ngAfterViewInit() {
    setInterval(() => {
      this.tickTock();
      this.difference = this.targetTime - this.now;
      this.difference = this.difference / (1000 * 60 * 60 * 24);

      !isNaN(this.days.nativeElement.innerText)
        ? (this.days.nativeElement.innerText = Math.floor(this.difference))
        : (this.days.nativeElement.innerHTML = `<img src="https://i.gifer.com/VAyR.gif" />`);
    }, 1000);
  }

  tickTock() {
    this.date = new Date();
    this.now = this.date.getTime();
    this.days.nativeElement.innerText = Math.floor(this.difference);
    this.hours.nativeElement.innerText = 23 - this.date.getHours();
    this.minutes.nativeElement.innerText = 60 - this.date.getMinutes();
    this.seconds.nativeElement.innerText = 60 - this.date.getSeconds();
  }
}
<body>
<div class="container">
    <div class="buttons">
        <button class="btn bg-transparent col-2 " routerLink="">Home</button>
        <button class="btn bg-transparent col-2" routerLink="/app-welcome">Cricket</button>
        <button class="btn bg-transparent col-2">Football</button>
        <button class="btn bg-transparent col-2" routerLink="/app-kabaddi" style="color: yellow;">Kabbadi</button>
    </div>
    <div class="times">
        <p #days></p>
        <p #hours></p>
        <p #minutes></p>
        <p #seconds></p>
    </div>
</div>
</body>
